function showLoader(){document.body.classList.add("loading")}function hideLoader(){const e=document.querySelector(".loader-wrapper");e&&(e.style.opacity="0",setTimeout((()=>{e.style.display="none",document.body.classList.remove("loading")}),500))}function initLoader(){showLoader(),window.addEventListener("load",(()=>{setTimeout(hideLoader,1e3)}))}document.addEventListener("DOMContentLoaded",initLoader);